import React, { useState } from "react";
import people from "../components/data";

const Review = () => {
  const [index, setIndex] = useState(0);
  const { name, job, image, text } = people[index];

  const checkNumber = (number) => {
    if (number > people.length - 1) {
      return 0;
    }
    if (number < 0) {
      return people.length - 1;
    }
    return number;
  };

  const nextPerson = () => {
    setIndex((index) => {
      let newIndex = index + 1;
      return checkNumber(newIndex);
    });
  };
  const prevPerson = () => {
    setIndex((index) => {
      let newIndex = index - 1;
      return checkNumber(newIndex);
    });
  };

  const randomPerson = () => {
    let randomNumber = Math.floor(Math.random() * people.length);
    if (randomNumber === index) {
      randomNumber = index + 1;
    }
    setIndex(checkNumber(randomNumber));
  };

  return (
    <article className="p-7 rounded text-center shadow-lg transition ease-in-out delay-50 bg-blue-500  hover:scale-110 hover:bg-indigo-500 duration-300">
      <div className="relative w-40 h-40 border rounded-full ">
        <img
          src={image}
          alt={name}
          className="w-full block h-full object-cover rounded-full relative"
        />
        <span className="quote-icon">{/* <FaQuoteRight /> */}</span>
      </div>
      <h4 className="m-1">{name}</h4>
      <p className="mb-1 uppercase text-sm">{job}</p>
      <p className="mb-1">{text}</p>
      <div className="button-container">
        <button className="prev-btn" onClick={prevPerson}>
          {/* <FaChevronLeft /> */} Left
        </button>
        <button className="next-btn" onClick={nextPerson}>
          {/* <FaChevronRight /> */} Right
        </button>
      </div>
      <button className="random-btn" onClick={randomPerson}>
        Suprise Me
      </button>
    </article>
  );
};

export default Review;
