const About = () => {
  return (
    <div className="flex ml-28">
      <div className=" flex flex-col pt-60 h-screen px-20 ">
        <h1 className=" h-40 text-8xl w-7/12 text-[#08fdd8] capitalize">
          Hey I'm nick
        </h1>
        <h1 className=" h-full text-3xl text-left w-7/12 text-[#f9f9f9]">
          I'm a young up start programmer making websites and perfecting my
          craft. Starting since i was a young child i always would fiddle and
          deconstruct computers. When I was a little older I started fixing them
          but also learning about the software on it as well. Fast forward until
          high school and was making website and writing code better than anyone
          I knew! As of now my goal is to perfect my craft while providing
          perfect service. In the future I dream of starting my own business and
          helping the youth of tomorrow do what I do know!
        </h1>
      </div>
    </div>
  );
};

export default About;
